import React, { useRef, useState } from "react";
import "./Portfolio.scss";
import ExperienceManager from "../../components/ExperiencesList";
import StudiesList from "../../components/StudiesList";
import CertificationsList from "../../components/CertificationsList";
import ProjectsList from "../../components/ProjectsList";
import useLanguage from "../../hook/useLanguage";
import PresentationsList from "../../components/PresentationList";
import HeaderInfo from "../../components/HeaderInfo/HeaderInfo";

const Portfolio = () => {
  const { isSpanish, setSpanish } = useLanguage();
  const personalProfileRef = useRef(null); // Paso 1: Crear referencia

  // Paso 2: Función para desplazarse a la sección
  const scrollToPersonalProfile = () => {
    personalProfileRef.current.scrollIntoView({ behavior: "smooth" });
  };

  return (
    <div className="external d-flex justify-content-center align-items-center min-vh-100">
      <div className="internal p-4 rounded">
        <div className="custom-div">
          <button
            onClick={() => setSpanish(!isSpanish)}
            className="btn btn-primary change-language"
          >
            {isSpanish ? "Switch to English" : "Cambiar a Español"}
          </button>
        </div>
        {/*  */}
        <HeaderInfo />
        <hr />
        <div className="test">
          <button className="btn btn-primary" onClick={scrollToPersonalProfile}>
            Personal profile
          </button>
        </div>
        <div className="test"><button className="btn btn-primary">Experience</button></div>
        <div className="test"><button className="btn btn-primary">Studies</button></div>
        <div className="test"><button className="btn btn-primary">Certifications</button></div>
        <div className="test"><button className="btn btn-primary">Projects</button></div>
        <hr />
        {/*  */}
        <h3>{isSpanish ? "Perfil personal" : "Personal profile"}</h3>
        <PresentationsList />
        <hr />
        {/*  */}
        <h3>{isSpanish ? "Experiencia" : "Experience"}</h3>
        <ExperienceManager />
        <hr />
        {/*  */}
        <h3>{isSpanish ? "Estudios" : "Studies"}</h3>
        <StudiesList />
        <hr />
        {/*  */}
        <h3>{isSpanish ? "Certificaciones" : "Certifications"}</h3>
        <CertificationsList />
        <hr />
        {/*  */}
        <h3>{isSpanish ? "Proyectos" : "Projects"}</h3>
        <ProjectsList />
        <hr />
        {/*  */}
      </div>
    </div>
  );
};

export default Portfolio;
