import React from 'react';

interface DownloadButtonProps {
  url: string;
  name: string;
  filePath: string;
}

const DownloadButton: React.FC<DownloadButtonProps> = ({ url, name, filePath }) => {
  return (
    <a
      href={url}
      className="btn d-inline-flex align-items-center"
      style={{ background: '#00F', borderColor: '#00F', color: '#fff', marginRight: '5px', marginBottom: '5px' }}
      target="_blank"
      rel="noopener noreferrer"
    >
      <img
        src={`${process.env.PUBLIC_URL}${filePath}`}
        alt={name}
        width="24"
        height="24"
        className="me-2"
      />
      {name}
    </a>
  );
};

export default DownloadButton;
