import React, { useState, useEffect } from 'react';

const RandomTextEffect: React.FC = () => {
  const originalText = "nito";
  const [displayText, setDisplayText] = useState("----");
  const [index, setIndex] = useState(0);

  const getSurroundingLetters = (char: string): string => {
    const alphabet = "abcdefghijklmnopqrstuvwxyz";
    const indexTemp = alphabet.indexOf(char.toLowerCase());

    if (indexTemp === -1) {
      return ""; // Retorna vacío si el carácter no es válido
    }

    let result = "";

    // Obtener 5 letras antes del carácter
    for (let i = 5; i > 0; i--) {
      result += alphabet[(indexTemp - i + alphabet.length) % alphabet.length];
    }

    // Añadir el carácter original
    result += char.toLowerCase();

    // Obtener 5 letras después del carácter
    for (let i = 1; i <= 5; i++) {
      result += alphabet[(indexTemp + i) % alphabet.length];
    }

    return result;
  };

  useEffect(() => {
    if (index < originalText.length) {
      const timeoutId = setTimeout(() => {
        const char = originalText[index];
        const chars = getSurroundingLetters(char);
        const nextChar = chars[Math.floor(Math.random() * chars.length)];

        // Actualizar displayText
        setDisplayText((prevText) => {
          const newTextArray = prevText.split('');
          newTextArray[index] = nextChar; // Cambiar el carácter actual
          return newTextArray.join('');
        });

        // Si el carácter generado coincide con el original, avanzar al siguiente
        if (nextChar === char) {
          setIndex((prevIndex) => prevIndex + 1);
        }

      }, 10);

      return () => clearTimeout(timeoutId); // Limpiar timeout al desmontar
    }
  }, [index, originalText]); // Dependencias: solo el índice y el texto original

  return (
    <div style={{ fontSize: '2rem', fontFamily: 'monospace', color: '#01D4F9' }}>
      {displayText}
    </div>
  );
};

export default RandomTextEffect;
