// src/store/useStore.ts
import create from 'zustand';

interface SquareStore {
  isOpen: boolean;
  toggleSquare: () => void;
}

export const useSquareStore = create<SquareStore>((set) => ({
  isOpen: false,
  toggleSquare: () => set((state) => ({ isOpen: !state.isOpen })),
}));
