import React from 'react';

interface DownloadButtonProps {
  url: string;
  name: string;
  iconPath: string;
  filePath: string; // Aquí se espera la ruta del PDF
}

const DownloadButton: React.FC<DownloadButtonProps> = ({ url, name, iconPath, filePath }) => {
  const handleDownload = (e: React.MouseEvent<HTMLAnchorElement>) => {
    e.preventDefault(); // Previene la acción por defecto del enlace
    const link = document.createElement('a');
    link.href = `${process.env.PUBLIC_URL}${filePath}`; // Ruta del archivo PDF
    link.target = '_blank'; // Abre en una nueva pestaña
    link.download = name; // Nombre del archivo al descargar
    link.click(); // Simula el clic para descargar
  };

  return (
    <a
      href={url} // Puedes dejar la URL original si la necesitas, o eliminarla si solo deseas que funcione como botón de descarga
      className="btn d-inline-flex align-items-center"
      style={{ background: '#00F', borderColor: '#00F', color: '#fff', marginRight: '5px', marginBottom: '5px' }}
      onClick={handleDownload} // Maneja el clic para descargar el PDF
      target="_blank"
      rel="noopener noreferrer"
    >
      <img
        src={`${process.env.PUBLIC_URL}${iconPath}`}
        alt={name}
        width="24"
        height="24"
        className="me-2"
      />
      {name}
    </a>
  );
};

export default DownloadButton;
