import React, { useState } from 'react';

interface SocialButtonProps {
  url: string;
  name: string;
  iconPath: string;
  backgroundColor: string;
}

const SocialButton: React.FC<SocialButtonProps> = ({ url, name, iconPath, backgroundColor }) => {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <a
      href={url}
      className="btn d-inline-flex align-items-center"
      style={{
        backgroundColor: isHovered ? '#17C9FD' : backgroundColor, // Cambia el color de fondo en hover
        borderColor: backgroundColor,
        color: '#fff',
        marginRight: '5px',
        marginBottom: '5px',
        transition: 'background-color 0.3s ease', // Transición suave
      }}
      target="_blank"
      rel="noopener noreferrer"
      onMouseEnter={() => setIsHovered(true)} // Al pasar el mouse, establece isHovered a true
      onMouseLeave={() => setIsHovered(false)} // Al salir el mouse, establece isHovered a false
    >
      <img
        src={`${process.env.PUBLIC_URL}${iconPath}`}
        alt={name}
        width="24"
        height="24"
        className="me-2"
      />
      {name}
    </a>
  );
};

export default SocialButton;
