import React, { useState, useEffect } from 'react';

const RandomTextEffect: React.FC = () => {
  const originalText = "nito";
  const [displayText, setDisplayText] = useState("----");

  useEffect(() => {
    let currentIndex = 0;
    let timeoutId: NodeJS.Timeout; // Almacena el ID del timeout para limpiarlo

    const randomizeChar = (): string => {
      const chars = "abcdefghijklmnopqrstuvwxyz";
      return chars[Math.floor(Math.random() * chars.length)];
    };

    const updateText = () => {
      if (currentIndex < originalText.length) {
        const nextChar = originalText[currentIndex];
        let tempText = displayText.split("");

        timeoutId = setTimeout(() => {
          const randomChar = randomizeChar();
          tempText[currentIndex] = randomChar;
          setDisplayText(tempText.join(""));

          if (randomChar === nextChar) {
            // Si llegamos al carácter correcto, avanzamos al siguiente
            currentIndex++;
            updateText(); // Actualizamos el siguiente carácter
          } else {
            updateText(); // Seguimos buscando el carácter correcto
          }
        }, 100);
      }
    };

    updateText();

    // Cleanup en caso de que el componente se desmonte
    return () => clearTimeout(timeoutId); // Se limpia correctamente con el ID del timeout
  }, [displayText]);

  return (
    <div style={{ fontSize: "2rem", fontFamily: "monospace", color: "#01D4F9" }}>
      {displayText}
    </div>
  );
};

export default RandomTextEffect;
