// App.tsx
import React, { useEffect } from 'react';
import { useLoadingStore } from '../hook/useLoadingStore';
import './App.css'; // Tus estilos
import Portfolio from '../pages/portfolio/Portfolio';

const App: React.FC = () => {
  const { isLoading, setLoading } = useLoadingStore();

  useEffect(() => {
    // Simula una carga de recursos (puedes reemplazarlo con tu lógica real)
    const timer = setTimeout(() => {
      setLoading(false);
    }, 2000); // 2 segundos simulando la carga

    return () => clearTimeout(timer);
  }, [setLoading]);

  return (
    <div>
      {isLoading ? (
        // Pantalla de carga
        <div className="loading-screen">
          <h1>Cargando...</h1>
        </div>
      ) : (
        // Contenido de la aplicación cuando esté todo listo
        <div className="app-content">
          <Portfolio/>
        </div>
      )}
    </div>
  );
};

export default App;
