import React, { useState } from "react";
import "./Portfolio.scss";
import ExperienceManager from "../../components/ExperiencesList";
import StudiesList from "../../components/StudiesList";
import CertificationsList from "../../components/CertificationsList";
import ProjectsList from "../../components/ProjectsList";
import useLanguage from "../../hook/useLanguage";
import PresentationsList from "../../components/PresentationList";

const Portfolio = () => {
  const { isSpanish, setSpanish } = useLanguage();

  return (
    <div className="external d-flex justify-content-center align-items-center min-vh-100">
      <div className="internal p-4 rounded">
        <div className="custom-div">
        <button onClick={() => setSpanish(!isSpanish)} className="btn btn-primary change-language">
          {isSpanish ? "Switch to English" : "Cambiar a Español"}
        </button>
        </div>
        {/*  */}
        <div className="profile_container">
          <img
            src={`${process.env.PUBLIC_URL}/assets/cv/profile_photo.png`}
            alt="Profile photo"
            className="profile_photo"
          />
          <div className="profile_text">
            <h1>Álvaro Crespi Liut</h1>
            <p>
              <span>
                {isSpanish
                  ? "Desarrollador Full-Stack | Ofreciendo soluciones de software de calidad."
                  : "Full-Stack Developer | Delivering Quality Software Solutions."}
              </span>
            </p>
            <a
              href="https://www.linkedin.com"
              className="btn btn-primary d-flex align-items-center"
              style={{ backgroundColor: '#0077B5', borderColor: '#0077B5' }}
              target="_blank"
              rel="noopener noreferrer"
            >
              {/* SVG del icono de LinkedIn */}
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="24"
                height="24"
                fill="currentColor"
                className="bi bi-linkedin me-2"
                viewBox="0 0 16 16"
              >
                <path d="M0 1.146C0 .513.526 0 1.175 0h13.65C15.474 0 16 .513 16 1.146v13.708c0 .633-.526 1.146-1.175 1.146H1.175C.526 16 0 15.487 0 14.854V1.146zM4.943 12.306V6.169H3.134v6.137h1.809zM4.089 5.13c.615 0 .998-.42.998-.945-.011-.536-.383-.945-.98-.945-.597 0-.998.409-.998.945 0 .525.383.945.98.945h.002zM12.306 12.306V8.776c0-.189-.013-.378-.066-.513-.144-.378-.472-.769-1.023-.769-.558 0-.816.406-.953.8-.052.129-.066.309-.066.489v3.523h1.81v-3.307c0-.176.012-.353.064-.484.144-.377.473-.768 1.025-.768.558 0 .816.406.953.8.052.129.065.308.065.488v3.526h-1.811z" />
              </svg>
              LinkedIn
            </a>
          </div>
        </div>
        <hr color="purple"/>
        {/*  */}
        <h3>{isSpanish ? "Perfil personal" : "Personal profile"}</h3>
        <PresentationsList />
        <hr />
        {/*  */}
        <h3>{isSpanish ? "Experiencia" : "Experience"}</h3>
        <ExperienceManager />
        <hr />
        {/*  */}
        <h3>{isSpanish ? "Estudios" : "Studies"}</h3>
        <StudiesList />
        <hr />
        {/*  */}
        <h3>{isSpanish ? "Certificaciones" : "Certifications"}</h3>
        <CertificationsList />
        <hr />
        {/*  */}
        <h3>{isSpanish ? "Proyectos" : "Projects"}</h3>
        <ProjectsList />
        <hr />
        {/*  */}
      </div>
    </div>
  );
};

export default Portfolio;
