import React, { useState, useEffect } from 'react';

const RandomTextEffect: React.FC = () => {
  const originalText = "nito";
  const [displayText, setDisplayText] = useState("----");

  const getSurroundingLetters = (char: string): string => {
    const alphabet = "abcdefghijklmnopqrstuvwxyz";
    const index = alphabet.indexOf(char.toLowerCase());
  
    if (index === -1) {
      return "Invalid character";
    }
  
    let result = "";

    for (let i = 5; i > 0; i--) {
      result += alphabet[(index - i + alphabet.length) % alphabet.length];
    }

    result += char.toLowerCase();

    for (let i = 1; i <= 5; i++) {
      result += alphabet[(index + i) % alphabet.length];
    }
  
    return result;
  };

  useEffect(() => {
    let currentIndex = 0;
    let timeoutId: NodeJS.Timeout;

    const updateText = () => {
        timeoutId = setTimeout(() => {
            const originalString = "nito";
            let index = 0;
            let nito = true;

            while (nito) {
                const char = originalString[index];
                
            }
        }, 100);
    };

    updateText();
    
    return () => clearTimeout(timeoutId);
  }, [displayText]);

  return (
    <>
        <div style={{ fontSize: "2rem", fontFamily: "monospace", color: "#01D4F9" }}>
        {displayText}
        </div>
        <div>
            <span>{getSurroundingLetters("a")}</span>
        </div>
    </>
  );
};

export default RandomTextEffect;
