import React, { useState } from "react";
import "./ExperienceCard.css";

interface ExperienceCardProps {
  title: string;
  description: string[];
  items: string[];
  technologies?: string[];
}

const ExperienceCard: React.FC<ExperienceCardProps> = ({
  title,
  description,
  items,
  technologies,
}) => {

  // Usamos useState para manejar el estado de 'show'
  const [show, setShow] = useState(true);

  // Funci贸n que cambia el estado de 'show'
  const handleTitleClick = () => {
    setShow(!show);  // Cambiamos el valor de 'show'
  };

  return (
    <div className="experience-card">
      <div className="title">
        <h6 onClick={handleTitleClick}>{title}</h6> {/* Evento onClick agregado al h6 */}
      </div>
      {show && (  // Renderizamos la descripci贸n solo si 'show' es true
        <div>
          <div className="description">
            <p>
              {description.map((text, index) => (
                <React.Fragment key={index}>{text}</React.Fragment>
              ))}
            </p>
          </div>
          <ul>
            {items.map((item, index) => (
              <li key={index}>{item}</li>
            ))}
          </ul>
          {technologies && technologies.length > 0 && (
            <div className="icons">
              {technologies.map((tech, index) => (
                <img
                  key={tech}
                  src={`${process.env.PUBLIC_URL}/assets/icons/technologies/${tech.toLowerCase()}.svg`}
                  alt={tech}
                  className="icon"
                  role="img"
                  aria-label={tech}
                />
              ))}
            </div>
          )}
          {technologies && technologies.length > 0 && (
            <div className="technologies-names">
              <span>[{technologies.join(", ")}]</span>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default ExperienceCard;
