// src/components/CollapsibleSquare.tsx
import React from 'react';
import { useSquareStore } from '../../hook/SquareStore';

const CollapsibleSquare: React.FC = () => {
  const { isOpen, toggleSquare } = useSquareStore();

  return (
    <div 
      style={{
        position: 'relative',
        width: '200px',
        height: '300px',
        border: '1px solid #ccc',
        overflow: 'hidden',
        display: 'flex',
        justifyContent: 'flex-start', // Alinear a la izquierda
        alignItems: 'center', // Centrado verticalmente
      }}
    >
      <div
        onClick={toggleSquare}
        style={{
          width: isOpen ? '100px' : '0px', // Ancho del cuadrado
          height: '100px', // Altura del cuadrado
          backgroundColor: 'blue',
          transition: 'width 0.3s ease', // Animación para el ancho
        }}
      />
    </div>
  );
};

export default CollapsibleSquare;
