import React from 'react';
import './HeaderItems.css';

interface HeaderItem {
  name: string;
  ref: React.RefObject<HTMLHeadingElement>;
}

interface HeaderItemsProps {
  items: HeaderItem[];
  scrollToSection: (ref: React.RefObject<HTMLHeadingElement>) => void; // Recibe la función como prop
}

const HeaderItems: React.FC<HeaderItemsProps> = ({ items, scrollToSection }) => {
  return (
    <header className="header-items">
      <nav className="header-nav">
        {items.map((item, index) => (
          <p
            key={index}
            className="header-item"
            onClick={() => scrollToSection(item.ref)} // Llama a la función aquí
          >
            {item.name}
          </p>
        ))}
      </nav>
    </header>
  );
};

export default HeaderItems;
