import { useState } from "react";
import ProjectsCard from "./ProjectsCard/ProjectsCard";
import useLanguage from "../hook/useLanguage";
import React from "react";

const ProjectsList: React.FC = () => {
  const { isSpanish, setSpanish } = useLanguage();
  const projects = [
    {
      title: isSpanish ? "AutoEscuela Santa Fe" : "Driving School Santa Fe",
      description: [
        isSpanish
          ? "Permite organizar horarios, registrar agendas de instructores, programar " +
            "clases, mantener datos de alumnos e instructores, gestionar pagos, generar reportes " +
            "estadísticos, llevar registros financieros y administrar el calendario de la autoescuela."
          : "It allows you to organize schedules, record instructor agendas, schedule classes, " +
            "maintain student and instructor data, manage payments, generate statistical reports, keep " +
            "financial records and manage the driving school calendar.",
      ],
      items: [
        isSpanish ? "Agenda inteligente" : "Smart agenda",
        isSpanish
          ? "Calendario y control de clases"
          : "Calendar and class control",
        isSpanish ? "Historias y estadísticas" : "Stories and statistics",
        isSpanish
          ? "Finanzas y control de pagos"
          : "Finance and payment control",
      ],
      technologies: [
        "HTML",
        "CSS",
        "Bootstrap",
        "Spring",
        "Java",
        "Maven",
        "PostgreSQL",
      ],
      imageSrc: [
        `${process.env.PUBLIC_URL}/assets/images/aesf/01.png`,
        `${process.env.PUBLIC_URL}/assets/images/aesf/02.png`,
        `${process.env.PUBLIC_URL}/assets/images/aesf/03.png`,
        `${process.env.PUBLIC_URL}/assets/images/aesf/04.png`,
        `${process.env.PUBLIC_URL}/assets/images/aesf/05.png`,
        `${process.env.PUBLIC_URL}/assets/images/aesf/06.png`,
        `${process.env.PUBLIC_URL}/assets/images/aesf/07.png`,
      ],
    },
    {
      title: isSpanish ? "AutoEscuela Santa Fe" : "Driving School Santa Fe",
      description: [
        isSpanish
          ? "Permite organizar horarios, registrar agendas de instructores, programar " +
            "clases, mantener datos de alumnos e instructores, gestionar pagos, generar reportes " +
            "estadísticos, llevar registros financieros y administrar el calendario de la autoescuela."
          : "It allows you to organize schedules, record instructor agendas, schedule classes, " +
            "maintain student and instructor data, manage payments, generate statistical reports, keep " +
            "financial records and manage the driving school calendar.",
      ],
      items: [
        isSpanish ? "Agenda inteligente" : "Smart agenda",
        isSpanish
          ? "Calendario y control de clases"
          : "Calendar and class control",
        isSpanish ? "Historias y estadísticas" : "Stories and statistics",
        isSpanish
          ? "Finanzas y control de pagos"
          : "Finance and payment control",
      ],
      technologies: ["JavaFX", "Spring", "Java", "Maven", "PostgreSQL"],
      imageSrc: [
        `${process.env.PUBLIC_URL}/assets/images/ceut/01.png`,
        `${process.env.PUBLIC_URL}/assets/images/ceut/02.png`,
        `${process.env.PUBLIC_URL}/assets/images/ceut/03.png`,
        `${process.env.PUBLIC_URL}/assets/images/ceut/04.png`,
        `${process.env.PUBLIC_URL}/assets/images/ceut/05.png`,
        `${process.env.PUBLIC_URL}/assets/images/ceut/06.png`,
        `${process.env.PUBLIC_URL}/assets/images/ceut/07.png`,
      ],
    },
    // agregar mas experiencias aca:
  ];

  return (
    <div>
      {projects.map((project, index) => (
        <React.Fragment key={index}>
          <ProjectsCard
            key={index}
            title={project.title}
            description={project.description}
            items={project.items}
            technologies={project.technologies}
            images={project.imageSrc}
          />
          {index < projects.length - 1 && <hr className="hr-gray" />}
        </React.Fragment>
      ))}
    </div>
  );
};

export default ProjectsList;
