import React, { useState, useEffect } from 'react';

const RandomTextEffect: React.FC = () => {
  let timeoutId: string | number | NodeJS.Timeout | undefined;
  const originalText = "nito";
  const [displayText, setDisplayText] = useState("----");
  const [index, setIndex] = useState(0);

  const getSurroundingLetters = (char: string): string => {
    const alphabet = "abcdefghijklmnopqrstuvwxyz";
    const indexTemp = alphabet.indexOf(char.toLowerCase());
  
    if (indexTemp === -1) {
      return "Invalid character";
    }
  
    let result = "";

    for (let i = 5; i > 0; i--) {
      result += alphabet[(indexTemp - i + alphabet.length) % alphabet.length];
    }

    result += char.toLowerCase();

    for (let i = 1; i <= 5; i++) {
      result += alphabet[(indexTemp + i) % alphabet.length];
    }
  
    return result;
  };

  const updateText = () => {
    if (displayText === originalText) {
      return; // Detener la actualizaci칩n si displayText ya es igual a originalText
    }

    timeoutId = setTimeout(() => {
      const char = originalText[index];
      const chars = getSurroundingLetters(char);
      let nextChar = chars[Math.floor(Math.random() * chars.length)];
      
      let newTextArray = displayText.split(""); // Convertir string a array
      newTextArray[index] = nextChar; // Modificar el array
      const newText = newTextArray.join(""); // Convertir array de nuevo a string

      setDisplayText(newText); // Actualizar estado

      // Si el car치cter coincide, avanzar al siguiente
      if (nextChar === char) {
        setIndex(prevIndex => prevIndex + 1);
      }

    }, 100);
  };

  useEffect(() => {
    if (index < originalText.length) {
      updateText();
    }

    return () => clearTimeout(timeoutId);
  }, [displayText, index]);

  return (
    <>
      <div style={{ fontSize: "2rem", fontFamily: "monospace", color: "#01D4F9" }}>
        {displayText}
      </div>
    </>
  );
};

export default RandomTextEffect;
